sys_security_acl.script:
// Deny write for non-admins when state is Closed or Cancelled

var isAdminLike =
    gs.hasRole('admin') ||
    gs.hasRole('x_1837836_hr_cas_1.hr_admin') ||
    gs.hasRole('x_1837836_hr_cas_0.admin');

if (isAdminLike) {
    answer = false; 
} else {

    var stateDisplay = current.state.getDisplayValue(); 

    if (stateDisplay == 'Closed' || stateDisplay == 'Cancelled') {

        answer = true;
    } else {

        answer = false;
    }
}
