sys_script.script:
(function executeRule(current, previous /*null when async*/) {

  // Guard conditions
  if (!current.u_ip_address) return;
  if (current.u_ip_address_info) return; // don't re-run if already filled

  try {
    var r = new sn_ws.RESTMessageV2('x_1837836_hr_cas_1.IP API Lookup', 'Default GET');
    r.setStringParameterNoEscape('query', current.u_ip_address);

    var response = r.execute();
    var body = response.getBody();
    var status = response.getStatusCode();

    gs.info('[IP LOOKUP] ' + current.number + ' ip=' + current.u_ip_address + ' status=' + status);

    if (status == 200 && body) {
      current.u_ip_address_info = body;

      // async BR needs update()
      current.setWorkflow(false); // prevents extra workflow noise
      current.update();
    } else {
      gs.warn('[IP LOOKUP] Non-200 or empty body. status=' + status + ' body=' + body);
    }

  } catch (ex) {
    gs.error('[IP LOOKUP] Exception: ' + ex.message);
  }

})(current, previous);
