sys_script_client.script:
function onLoad() {
    // يشتغل فقط إذا كان السجل جديد
    if (!g_form.isNewRecord()) {
        return;
    }

    // 1) إذا الحقل Caller فاضي، خلي caller = المستخدم الحالي
    if (!g_form.getValue('caller')) {
        g_form.setValue('caller', g_user.userID);
    }

    // 2) بعد ما نتأكد من الـ Caller، نجيب بياناته (الإيميل + القسم)
    var callerId = g_form.getValue('caller');

    if (!callerId) {
        // لو لسبب ما ما فيه Caller، فضّي الحقول
        g_form.setValue('email', '');
        g_form.setValue('department', '');
        return;
    }

    g_form.getReference('caller', function(user) {
        // Email
        if (user.email) {
            g_form.setValue('email', user.email);
        } else {
            g_form.setValue('email', '');
        }

        // Department
        if (user.department) {
            g_form.setValue('department', user.department);
        } else {
            g_form.setValue('department', '');
        }
    });
}
