sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
  if (isLoading) return;

  // Category is a CHOICE field, so newValue will be: leave/travel/onboarding...
  var map = {
    "leave":       "f0b8bf2547a1b610d1321af2e16d4376",
    "offboarding": "db98fb2547a1b610d1321af2e16d43a4",
    "onboarding":  "5358f32547a1b610d1321af2e16d4397",
    "others":      "70c833614761b610d1321af2e16d4338",
    "payroll":     "dda83f2547a1b610d1321af2e16d4381",
    "travel":      "e6b8ff2547a1b610d1321af2e16d4353"
  };

  // Clear if category cleared
  if (!newValue) {
    g_form.setValue("assignment_group", "");
    return;
  }

  var groupId = map[newValue];

  // If no match, clear it (or remove this line if you prefer keeping current value)
  g_form.setValue("assignment_group", groupId || "");
}
