sys_script_client.script:
function onChange(control, oldValue, newValue) {

    // When category is Incident
    if (newValue == 'incident') {

        // Show error message
        g_form.showFieldMsg('category',
            'Incident cases cannot use subcategories.',
            'error');

        // Hide subcategory + clear + make not mandatory
        g_form.setValue('subcategory', '');
        g_form.setMandatory('subcategory', false);
        g_form.setDisplay('subcategory', false);

    } else {

        // Restore normal behavior
        g_form.setDisplay('subcategory', true);
        g_form.setMandatory('subcategory', true);
    }
}
