sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    // استخدم القيمة الحالية للـ Category سواء في اللود أو التغيير
    var cat = g_form.getValue('category');

    // لو ما فيه قيمة، رجّع الحقل عادي وغير إلزامي
    if (!cat) {
        g_form.setVisible('subcategory', true);
        g_form.setMandatory('subcategory', false);
        return;
    }

    // لو Incident → نخفي Subcategory تماماً
    if (cat == 'incident') {
        // امسحي أي قيمة موجودة
        g_form.clearValue('subcategory');

        // شيل الإلزام وأخفي الحقل
        g_form.setMandatory('subcategory', false);
        g_form.setVisible('subcategory', false);

        g_form.addInfoMessage(
            'For Incidents, please use the standard Incident module instead of HR Case.'
        );
    } else {
        // أي Category ثانية → أظهري Subcategory وخليه mandatory
        g_form.setVisible('subcategory', true);
        g_form.setMandatory('subcategory', true);
    }
}
